mapper.select:
select

from Cause where delFlag=0
and causeName = #{causeName,jdbcType=VARCHAR}

    	 Cause
    	
    		delFlag=0
    		
				and parentId = #{parentId}
			
			
   			 	and parentId in
		      	
		            #{parentId}
		        
  			
  			
			
				and causeType = #{causeType}
			
			
   			 	and causeType in
		      	
		            #{causeType}
		        
  			
  			
    		
				and causeName = #{causeName}
			
			
   			 	and causeName in
		      	
		            #{causeName}
		        
  			
  			
			
				and occurrencedTime=#{occurrencedTime}
			
			
			
				and endTime=#{endTime}
			
			
			
				and lastModifyTime=#{lastModifyTime}
			
			
			
				and createdTime=#{createdTime}
			
			
				
				and createdTime>=#{createdTimeBegin}
				     
			
			
				
				and createdTime<=#{createdTimeEnd}
				     
			
			
			
				and owner=#{owner}
			
			
			
				and createdBy=#{createdBy}
			
			
			
				and updatedBy=#{updatedBy}
			
			
			
				 and remark like CONCAT('%','${remark}','%' )

