mapper.select:
select

from expenditure where delFlag=0
and expenditureAmount = #{expenditureAmount,jdbcType=DECIMAL}

    	 expenditure
    	
    		delFlag=0
    		
				and expenditureAmount = #{expenditureAmount}
			
			
  			
			
				and occurrencedTime=#{occurrencedTime}
			
			
			
				and endTime=#{endTime}
			
			
			
				and lastModifyTime=#{lastModifyTime}
			
			
			
				and createdTime=#{createdTime}
			
			
				
				and createdTime>=#{createdTimeBegin}
				     
			
			
				
				and createdTime<=#{createdTimeEnd}
				     
			
			
			
				and owner=#{owner}
			
			
			
				and createdBy=#{createdBy}
			
			
			
				and updatedBy=#{updatedBy}
			
			
			
				 and remark like CONCAT('%','${remark}','%' )

    	select  from expenditure 
    	
    		delFlag=0
    		
				and expenditureAmount = #{expenditureAmount}
			
			
				
				and expenditureAmount >= #{expenditureAmountBegin}
				   
			
  			
				
				and expenditureAmount <= #{expenditureAmountEnd}
				   
			
			
			
				and occurrencedTime=#{occurrencedTime}
			
			
				
				and occurrencedTime>=#{occurrencedTimeBegin}
				     
			
			
				
				and occurrencedTime<=#{occurrencedTimeEnd}
				     
			
			
			
				and endTime=#{endTime}
			
			
				
				and endTime>=#{endTimeBegin}
				     
			
			
				
				and endTime<=#{endTimeEnd}
				     
			
			
			
				and lastModifyTime=#{lastModifyTime}
			
			
			
				and createdTime=#{createdTime}
			
			
				
				and createdTime>=#{createdTimeBegin}
				     
			
			
				
				and createdTime<=#{createdTimeEnd}
				     
			
			
			
				and owner=#{owner}
			
			
   			 	and owner in
		      	
		            #{owner}
		        
  			
  			
			
				and createdBy=#{createdBy}
			
			
   			 	and createdBy in
		      	
		            #{createdBy}
		        
  			
  			
			
				and updatedBy=#{updatedBy}
			
			
   			 	and createdBy in
		      	
